
public class countGC {

	 	public static String myMethod (){
	 		return "hi";
	 	}
	 	
		public static void main(String[] args) {
	      
			String var = myMethod();
			System.out.println(var);
		 	//String lattice =""; 		 	
		 	//re-arranged
		 	String lattice ="";		 	
		 	//original
		 	//String lattice = "";

			 	
		 	lattice = lattice.toUpperCase();
		 	int word_size = 544;
		 	String current ="";
		 	double GC_count=0;
		 	int counter=0;
		 	int overall_counter=0;
		 	System.out.println("Seq length ="+lattice.length());
		 	
		 	for (int i=0;i<(lattice.length())/word_size;i++){
		 		
		 		current =  lattice.substring(i*word_size,  (i*word_size) +word_size);
		 		
		 		GC_count = 0;
		 		for (int j=0;j<current.length();j++){
		 			
		 			if (current.charAt(j) == 'G' || current.charAt(j) == 'C')
		 				GC_count++;
		 		}
		 		System.out.println("word no. "+(++counter)+": "+(GC_count/word_size)*100);
		 		overall_counter +=GC_count;
		 	}
		 	GC_count=0;
		 	current =  lattice.substring(((lattice.length())/word_size)*word_size,  lattice.length());
		 	for (int j=0;j<current.length();j++){
	 			
	 			if (current.charAt(j) == 'G' || current.charAt(j) == 'C')
	 				GC_count++;
	 		}
		 	overall_counter +=GC_count;
		 	//System.out.println("\n\n Overall GC % = "+(((double)overall_counter)/lattice.length())*100);
	 }
}
